// The entire code is wrapped in an Immediately Invoked Function Expression (IIFE) to avoid polluting the global scope
!function(n) {
    // Remove the "no-js" class from the HTML element to indicate JavaScript is enabled
    n("html").removeClass("no-js");
    
    // Click event handler for all anchor tags inside the header
    n("header a").click(function(e) {
        if (!n(this).hasClass("no-scroll")) {
            e.preventDefault();
            var t = n(this).attr("href");
            var i = n(t).offset().top;
            n("html, body").animate({scrollTop: i + "px"}, +Math.abs(window.pageYOffset - n(t).offset().top));
            if (n("header").hasClass("active")) {
                n("header, body").removeClass("active");
            }
        }
    });
    
    // Click event handler for the element with ID "to-top" to scroll to the top
    n("#to-top").click(function() {
        n("html, body").animate({scrollTop: 0}, 500);
    });
    
    // Click event handler for the span inside the element with ID "lead-down" to scroll to the next section
    n("#lead-down span").click(function() {
        var e = n("#lead").next().offset().top;
        n("html, body").animate({scrollTop: e + "px"}, 500);
    });
    
    // For each element with the ID "experience-timeline"
    n("#experience-timeline").each(function() {
        $this = n(this);
        $userContent = $this.children("div");
        
        // Wrap each child div with a specific structure and add classes
        $userContent.each(function() {
            n(this).addClass("vtimeline-content").wrap('<div class="vtimeline-point"><div class="vtimeline-block"></div></div>');
        });
        
        // Prepend an icon to each "vtimeline-point"
        $this.find(".vtimeline-point").each(function() {
            n(this).prepend('<div class="vtimeline-icon"><i class="fa fa-map-marker"></i></div>');
        });
        
        // Add a date to each "vtimeline-content" if data-date attribute is present
        $this.find(".vtimeline-content").each(function() {
            var e = n(this).data("date");
            if (e) {
                n(this).parent().prepend('<span class="vtimeline-date">' + e + "</span>");
            }
        });
    });
    
    // Click event handler for opening the mobile menu
    n("#mobile-menu-open").click(function() {
        n("header, body").addClass("active");
    });
    
    // Click event handler for closing the mobile menu
    n("#mobile-menu-close").click(function() {
        n("header, body").removeClass("active");
    });
    
    // Click event handler for "view more projects" button
    n("#view-more-projects").click(function(e) {
        e.preventDefault();
        n(this).fadeOut(300, function() {
            n("#more-projects").fadeIn(300);
        });
    });
}(jQuery);
